def generate_sentence() -> Tuple[str, List[Tuple[int, int, str]]]:
    template = templates[0]
    templates.rotate(-1)
    depart = cities[0]
    cities.rotate(-1)
    arrivee = cities[0]
    cities.rotate(-1)
    sentence = template.format(depart=depart, arrivee=arrivee)
    doc = nlp(sentence)
    entities = []
    for token in doc:
        if token.text == depart:
            entities.append((token.idx, token.idx + len(token.text), "DEPARTURE"))
        elif token.text == arrivee:
            entities.append((token.idx, token.idx + len(token.text), "ARRIVAL"))
    return sentence, entities

def create_sentence(sentence_data: Dict) -> bool:
    """Create a single sentence via API request"""
    try:
        response = requests.post(
            f"{API_BASE_URL}/sentences",
            json=sentence_data,
            headers={"Content-Type": "application/json"}
        )
        response.raise_for_status()
        return True
    except requests.exceptions.RequestException as e:
        print(f"Error creating sentence: {e}")
        return False

def process_batch(sentences: List[Dict]) -> int:
    """Process a batch of sentences using thread pool"""
    successful = 0
    with ThreadPoolExecutor(max_workers=10) as executor:
        # Submit all requests
        future_to_sentence = {
            executor.submit(create_sentence, sentence): sentence 
            for sentence in sentences
        }
        
        # Process completed requests
        for future in as_completed(future_to_sentence):
            if future.result():
                successful += 1
    
    return successful

def insert_sentences(n: int):
    successful_inserts = 0
    batch = []
    
    # Process one of each template first
    num_templates = len(templates)
    print(f"Inserting {num_templates} template sentences...")
    
    for _ in range(num_templates):
        text, entities = generate_sentence()
        sentence_data = {
            "text": text,
            "entities": [{"start": start, "end": end, "label": label} for start, end, label in entities],
            "isTreated": True
        }
        batch.append(sentence_data)
        
        if len(batch) >= BATCH_SIZE:
            successful_inserts += process_batch(batch)
            batch = []
    
    # Process remaining sentences
    remaining = n - num_templates
    with tqdm(total=remaining, desc="Inserting sentences") as pbar:
        while remaining > 0:
            # Generate sentences for current batch
            batch_size = min(BATCH_SIZE, remaining)
            batch = []
            
            for _ in range(batch_size):
                text, entities = generate_sentence()
                sentence_data = {
                    "text": text,
                    "entities": [{"start": start, "end": end, "label": label} for start, end, label in entities],
                    "isValid": False,
                    "isTreated": True
                }
                batch.append(sentence_data)
            
            # Process the batch
            successful_batch = process_batch(batch)
            successful_inserts += successful_batch
            remaining -= batch_size
            pbar.update(batch_size)
            
            # Add a small delay between batches to avoid overwhelming the server
            time.sleep(0.1)
    
    return successful_inserts

